mm-config.json<pre><code>{
    "name": "ORDER Market Maker",
    "symbol": "ORDER",

    "market_making": {
        "base_spread_bps": 30,
        "min_spread_bps": 15,
        "order_size_usd": 50,
        "max_inventory_usd": 300,
        "levels": 1,
        "skew_per_100_usd": 5,
        "requote_interval_sec": 30
    },

    "risk": {
        "max_position_usd": 500,
        "stop_loss_pct": 5,
        "daily_loss_limit_usd": 50
    },

    "execution": {
        "post_only": true,
        "min_edge_bps": 5
    },

    "flags": {
        "dry_run": true,
        "log_quotes": true
    }
}
</code></pre>

simple-strategy.json<pre><code>{
    "name": "ETH Momentum",
    "version": "1.0.0",
    "description": "RSI-based ETH trading",
    "symbol": "ETH",
    "timeframe": "4h",

    "signals": {
        "period": 14,
        "long_entry": 30,
        "short_entry": 70
    },

    "position": {
        "leverage": 5,
        "size_pct": 10
    },

    "risk": {
        "stop_loss_pct": 5,
        "take_profit_pct": 15,
        "max_positions": 1
    },

    "flags": {
        "dry_run": false,
        "allow_shorts": true
    }
}
</code></pre>

multi-asset-strategy.json<pre><code>{
    "name": "Unlockoor",
    "version": "2.0.0",
    "description": "Multi-asset RSI strategy",

    "long_assets": ["BTC", "ETH", "SOL"],
    "short_assets": ["DOGE", "SHIB"],

    "timeframe": "4h",

    "signals": {
        "period": 14,
        "long_entry": 30,
        "short_entry": 70
    },

    "position": {
        "leverage": 3,
        "size_pct": 5
    },

    "risk": {
        "stop_loss_pct": 8,
        "take_profit_pct": 20,
        "max_positions": 5
    },

    "flags": {
        "dry_run": false,
        "allow_shorts": true
    }
}
</code></pre>

